    private void btnRegistrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegistrarActionPerformed
        String cedulaProp = (String) cmbCedulaPropietario.getSelectedItem();
        String ciudad = (String) cmbCiudad.getSelectedItem();
        String estVivienda = (String) cmbEstVivienda.getSelectedItem();
        String tipoVivienda = (String) cmbTipoVivienda.getSelectedItem();

        String idVivienda = txtIDVivienda.getText().trim();
        String nombre = txtNombreVivienda.getText().trim();
        String email = txtEmail.getText().trim();
        String direccion = txtDireccion.getText().trim();

        String numMaxP = txtMaxPer.getText().trim();
        String numHbt = txtNumHabt.getText().trim();
        String numHbtOc = txtNumHbtOc.getText().trim();
        String numHbtDs = txtNumHbtDis.getText().trim();

        if (admVi.validarCombos(cmbCedulaPropietario, cmbCiudad, cmbEstVivienda, cmbTipoVivienda)
                && admVi.validarCajas(idVivienda, nombre, email, direccion, numMaxP, numHbt, numHbtOc, numHbtDs)
                && admVi.validarDatos(idVivienda, nombre, numMaxP, numHbt, numHbtOc, numHbtDs)) {
            admVi.registrarVivienda(cedulaProp, ciudad, estVivienda, tipoVivienda, idVivienda, nombre, email, direccion, numMaxP, numHbt, numHbtOc, numHbtDs);
            admVi.cargarTabla(tblVivienda);
            System.out.println("registrado");

        }
    }//GEN-LAST:event_btnRegistrarActionPerformed

