    private void btnEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEliminarActionPerformed
        if (admVi.selectFila(tblVivienda)) {
            admVi.eliminarVivienda(txtIDVivienda.getText().trim());
            admVi.limpiarCampos(txtDireccion, txtEmail, txtIDVivienda, txtMaxPer, txtNombreVivienda, txtNumHabt, txtNumHbtDis, txtNumHbtOc, txtPropietario,
                    cmbCedulaPropietario, cmbCiudad, cmbEstVivienda, cmbTipoVivienda, tblVivienda);
            admVi.cargarTabla(tblVivienda);
        }
    }//GEN-LAST:event_btnEliminarActionPerformed

